// Identifica cliente, agente e canal
const payload = $input.item.json;

// DEBUG: Log payload recebido
console.log('=== DEBUG: Estrutura completa do payload ===');
console.log('payload:', JSON.stringify(payload, null, 2));
console.log('payload.body:', JSON.stringify(payload.body, null, 2));
console.log('payload.content:', payload.content);
console.log('payload.body?.content:', payload.body?.content);

// Extrair informaÃ§Ãµes do webhook Chatwoot
// ROBUSTEZ: Tenta todas as possibilidades de estrutura
const conversationId = payload.body?.conversation?.id || payload.conversation?.id;
const contactId = payload.body?.sender?.id || payload.sender?.id;

// CRÃTICO: message_body com extraÃ§Ã£o robusta (ordem importa!)
const messageBody = 
  payload.content ||              // Webhook n8n (nivel raiz)
  payload.body?.content ||        // Chatwoot direto
  payload.body?.body?.content ||  // Chatwoot encapsulado
  '';

console.log('âœ… message_body extraÃ­do:', messageBody);

// VALIDAÃ‡ÃƒO: Abortar se message_body vazio
if (!messageBody || messageBody.trim() === '') {
  console.error('âŒ ERRO CRÃTICO: message_body estÃ¡ vazio ou undefined!');
  console.error('Payload keys:', Object.keys(payload));
  console.error('Payload.body keys:', payload.body ? Object.keys(payload.body) : 'body is undefined');
  throw new Error('message_body nÃ£o pode estar vazio. Verifique estrutura do payload.');
}

// CRÃTICO: Converter message_type de nÃºmero para string
// Chatwoot envia: 0=incoming, 1=outgoing, 2=activity
let messageType = payload.message_type || payload.body?.message_type || 'incoming';
if (typeof messageType === 'number') {
  messageType = messageType === 0 ? 'incoming' : messageType === 1 ? 'outgoing' : 'activity';
}
console.log('âœ… message_type convertido:', messageType);

const contentType = payload.content_type || payload.body?.content_type || 'text';

// Extrair custom attributes (onde guardamos client_id e agent_id)
const customAttributes = payload.conversation?.custom_attributes || payload.body?.conversation?.custom_attributes || {};
const clientId = customAttributes.client_id || 'clinica_sorriso_001';
const agentId = customAttributes.agent_id || 'default';

// Canal (whatsapp, email, instagram, etc)
const channel = payload.inbox?.channel_type || payload.body?.inbox?.channel_type || 'whatsapp';

// Attachments (mÃ­dia que USUÃRIO enviou)
const attachments = payload.attachments || payload.body?.attachments || [];

return {
  json: {
    client_id: clientId,
    agent_id: agentId,
    conversation_id: conversationId,
    contact_id: contactId,
    channel: channel,
    message_body: messageBody,
    message_type: messageType,
    content_type: contentType,
    attachments: attachments,
    has_attachments: attachments.length > 0,
    timestamp: new Date().toISOString(),
    original_payload: payload
  }
};

// Construir contexto completo: dados do agente + mÃ­dia do acervo + mensagem original
// CRÃTICO: O Merge combina mÃºltiplos inputs, mas precisamos buscar dados do node correto

const item = $input.item.json;

// Buscar dados originais do webhook do node 'Filtrar Apenas Incoming'
// O Merge preserva os dados, mas precisamos garantir que message_body nÃ£o seja sobrescrito
const webhookNode = $('Filtrar Apenas Incoming').first().json;

const webhookData = {
  client_id: webhookNode.client_id || item.client_id,
  agent_id: webhookNode.agent_id || item.agent_id,
  conversation_id: webhookNode.conversation_id || item.conversation_id,
  contact_id: webhookNode.contact_id || item.contact_id,
  channel: webhookNode.channel || item.channel,
  message_body: webhookNode.message_body || item.message_body,  // âœ… BUSCA DO NODE CORRETO
  message_type: webhookNode.message_type || item.message_type,
  content_type: webhookNode.content_type || item.content_type,
  attachments: webhookNode.attachments || item.attachments || [],
  has_attachments: webhookNode.has_attachments || item.has_attachments || false,
  timestamp: webhookNode.timestamp || item.timestamp
};

// VALIDAÃ‡ÃƒO: Abortar se message_body vazio
if (!webhookData.message_body || webhookData.message_body.trim() === '') {
  console.error('âŒ ERRO: message_body estÃ¡ vazio em Construir Contexto Completo!');
  console.error('webhookNode.message_body:', webhookNode.message_body);
  console.error('item.message_body:', item.message_body);
  throw new Error('message_body perdido no fluxo!');
}

console.log('âœ… message_body preservado:', webhookData.message_body);

const agentData = item; // Dados do agente jÃ¡ estÃ£o no item merged

// Verificar se hÃ¡ dados de mÃ­dia no item (do RPC)
let mediaRules = [];
if (item.rule_id && item.media_id) {
  // RPC retornou mÃ­dia - transformar em array
  mediaRules = [{
    rule_id: item.rule_id,
    media_id: item.media_id,
    trigger_type: item.trigger_type,
    trigger_value: item.trigger_value,
    file_url: item.file_url,
    file_type: item.file_type,
    file_name: item.file_name,
    mime_type: item.mime_type,
    title: item.title,
    description: item.description
  }];
}

// Preparar informaÃ§Ã£o sobre mÃ­dia do acervo para o LLM
let mediaContext = '';
let clientMediaAttachments = [];
let mediaLogEntries = [];

if (Array.isArray(mediaRules) && mediaRules.length > 0) {
  mediaContext = '\n\n--- MÃDIA DISPONÃVEL PARA ENVIO ---\n';

  mediaRules.forEach((media, i) => {
    mediaContext += `${i+1}. ${media.title || media.file_name} (${media.file_type})\n`;
    mediaContext += `   DescriÃ§Ã£o: ${media.description || 'N/A'}\n`;
    mediaContext += `   Disparado por: ${media.trigger_type} - "${media.trigger_value}"\n\n`;

    // Preparar attachments para Chatwoot
    clientMediaAttachments.push({
      file_url: media.file_url,
      file_type: media.file_type,
      file_name: media.file_name,
      caption: media.title || media.description || ''
    });

    // Preparar logs para tracking
    mediaLogEntries.push({
      rule_id: media.rule_id,
      media_id: media.media_id,
      triggered_by: media.trigger_type,
      trigger_value: media.trigger_value
    });
  });

  mediaContext += '\nðŸš¨ INSTRUÃ‡ÃƒO OBRIGATÃ“RIA: VocÃª DEVE informar ao usuÃ¡rio que estÃ¡ enviando o arquivo anexo mencionado acima! NÃ£o ignore esta instruÃ§Ã£o!';
}

return {
  json: {
    // Preservar TODOS os dados originais do webhook (incluindo message_body)
    client_id: webhookData.client_id,
    agent_id: webhookData.agent_id,
    conversation_id: webhookData.conversation_id,
    contact_id: webhookData.contact_id,
    channel: webhookData.channel,
    message_body: webhookData.message_body,  // âœ… IMPORTANTE!
    message_type: webhookData.message_type,
    content_type: webhookData.content_type,
    attachments: webhookData.attachments,
    has_attachments: webhookData.has_attachments,
    timestamp: webhookData.timestamp,

    // Dados do agente
    system_prompt: agentData.system_prompt || 'VocÃª Ã© um assistente Ãºtil.',
    llm_model: agentData.llm_model || 'gpt-4o-mini',
    tools_enabled: agentData.tools_enabled || [],
    rag_namespace: agentData.rag_namespace,

    // Contexto de mÃ­dia do acervo
    media_context: mediaContext,
    client_media_attachments: clientMediaAttachments,
    media_log_entries: mediaLogEntries,
    has_client_media: clientMediaAttachments.length > 0,

    // Subscription data (para usage tracking)
    subscription: agentData.client_subscriptions?.[0] || {}
  }
};

// Query no RAG usando namespace isolado (PLACEHOLDER - Integrar com Pinecone/Qdrant)
