const mediaContext = $input.item.json.media_context || '';

// CRÃTICO: Se hÃ¡ mÃ­dia disponÃ­vel, injetar instruÃ§Ã£o no SYSTEM PROMPT
if (mediaContext && mediaContext.includes('MÃDIA DISPONÃVEL')) {
  systemPrompt += '\n\n--- INSTRUÃ‡ÃƒO CRÃTICA SOBRE MÃDIA ---\n';
  systemPrompt += 'Se houver arquivos de mÃ­dia disponÃ­veis mencionados no contexto do usuÃ¡rio, vocÃª DEVE informar que estÃ¡ enviando esses arquivos como anexo. NÃ£o ignore esta instruÃ§Ã£o!';
}

const fullPrompt = systemPrompt + 
